/* Vendored minimal mermaid shim for CI/dev.
   This is a small wrapper that lazily loads the real mermaid from CDN when
   executed. You can replace this file with a full mermaid.min.js to avoid
   network access in CI. */
(function(){
  if (window.mermaid) return
  // Load the full runtime from CDN (this keeps the repo small). If you want
  // an offline experience, replace this file with the official mermaid
  // bundle (mermaid.min.js).
  var s = document.createElement('script')
  s.src = 'https://unpkg.com/mermaid@10.4.0/dist/mermaid.min.js'
  s.async = true
  s.onload = function(){
    if (window.mermaid && window.mermaid.initialize) {
      window.mermaid.initialize({ startOnLoad: false })
    }
  }
  s.onerror = function(){
    console.warn('mermaid loader failed')
  }
  document.head.appendChild(s)
})()
